// @flow

import parser from './parser'

export default parser

// @flow
export type IISOString = string

export type IISODate = {
  constructor(input: IInput): IISODate
}

export type IInput = string

export type IPart = string
export type IParts = Array<IPart>
export type IBuilder = (parts: IParts, date: Date) => Date

export type IParser = {
  pattern: Array<RegExp>,
  builder: IBuilder
}

// @flow

import type {IParser} from '../interface'
import {correctOffset, getSignFactor} from './util'

// 4.3 Date and time of day
/**
 * @ignore
 */
const parser: IParser = {
  // (YYYYMMDD | YYYYDDD | YYYYWwwD) T? HHMMSSTZ
  // The expression shall either be completely in basic format or completely in extended format
  pattern: [
    /^(\d{4}|[\-+]\d{6})(?:(0[1-9]|1[012])(0[1-9]|[12]\d|30|31)|(00[1-9]|0[1-9]\d|[12]\d\d|3[0-5]\d|36[0-6])|W(0[1-9]|[1-4]\d|5[0-3])(\d))T(?:(24)|(24)(00)|(24)(00)(00))(?:(Z)|([\-+])(\d{2})(\d{2})?)?$/,
    /^(\d{4}|[\-+]\d{6})-(?:(0[1-9]|1[012])-(0[1-9]|[12]\d|30|31)|(00[1-9]|0[1-9]\d|[12]\d\d|3[0-5]\d|36[0-6])|W(0[1-9]|[1-4]\d|5[0-3])-(\d))T(?:(24)|(24):(00)|(24):(00):(00))(?:(Z)|([\-+])(\d{2})(?::(\d{2}))?)?$/,
    /^(\d{4}|[\-+]\d{6})(?:(0[1-9]|1[012])(0[1-9]|[12]\d|30|31)|(00[1-9]|0[1-9]\d|[12]\d\d|3[0-5]\d|36[0-6])|W(0[1-9]|[1-4]\d|5[0-3])(\d))T(?:((?:[01]\d|2[0-3])(?:\.\d+)?)|([01]\d|2[0-3])([0-5]\d(?:\.\d+)?)|([01]\d|2[0-3])([0-5]\d)((?:[0-5]\d|60)(?:\.\d+)?))(?:(Z)|([\-+])(\d{2})(\d{2})?)?$/,
    /^(\d{4}|[\-+]\d{6})-(?:(0[1-9]|1[012])-(0[1-9]|[12]\d|30|31)|(00[1-9]|0[1-9]\d|[12]\d\d|3[0-5]\d|36[0-6])|W(0[1-9]|[1-4]\d|5[0-3])-(\d))T(?:((?:[01]\d|2[0-3])(?:\.\d+)?)|([01]\d|2[0-3]):([0-5]\d(?:\.\d+)?)|([01]\d|2[0-3]):([0-5]\d):((?:[0-5]\d|60)(?:\.\d+)?))(?:(Z)|([\-+])(\d{2})(?::(\d{2}))?)?$/
  ],
  builder (parts): Date {
    const year = +parts[1]
    const hours = +(parts[7] || parts[8] || parts[10])
    const minutes = +(parts[9] || parts[11] || 0) + 60 * (hours - (hours | 0))
    const seconds = +(parts[12] || 0) + 60 * (minutes - (minutes | 0))
    const milliseconds = Math.round(1000 * (seconds - (seconds | 0)))
    let month = 0
    let day

    switch (true) {
      case parts[2] !== undefined:
        month = +parts[2] - 1
        day = +parts[3]
        break

      case parts[4] !== undefined:
        day = +parts[4]
        break

      default:
        // NOTE 2.2.10 calendar week number
        // "first calendar week of a year is that one which includes the first Thursday of that year"
        day = +parts[6] + 7 * +parts[5] - 7 * +(new Date(year, 0, 1).getDay() < 5) - new Date(year, 0, 1).getDay() + 1
    }

    if (parts[13]) { // Zulu time
      return new Date(Date.UTC(year, month, day, hours, minutes, seconds, milliseconds))
    }

    const date = new Date(year, month, day, hours, minutes, seconds, milliseconds)
    const offset = getSignFactor(parts[14]) * (+parts[15] * 60 + (+parts[16] || 0))

    correctOffset(date, offset)
    return date
  }
}

export default parser

// @flow

import type {IParser} from '../interface'
import {correctOffset, getSignFactor} from './util'

// NOTE 4.2.2.4 Representations with decimal fraction: "A decimal fraction shall have at least one digit"
/**
 * @ignore
 */
const parser: IParser = {
  pattern: [
    /* AB AC */
    /^T?(?:(24)|(24)(00)|(24)(00)(00))(?:(Z)|([\-+])(\d{2})(\d{2})?)?$/,
    /^T?(?:(24)|(24):(00)|(24):(00):(00))(?:(Z)|([\-+])(\d{2})(?::(\d{2}))?)?$/,
    /^T?(?:((?:[01]\d|2[0-3])(?:\.\d+)?)|([01]\d|2[0-3])([0-5]\d(?:\.\d+)?)|([01]\d|2[0-3])([0-5]\d)((?:[0-5]\d|60)(?:\.\d+)?))(?:(Z)|([\-+])(\d{2})(\d{2})?)?$/,
    /^T?(?:((?:[01]\d|2[0-3])(?:\.\d+)?)|([01]\d|2[0-3]):([0-5]\d(?:\.\d+)?)|([01]\d|2[0-3]):([0-5]\d):((?:[0-5]\d|60)(?:\.\d+)?))(?:(Z)|([\-+])(\d{2})(?::(\d{2}))?)?$/
  ],
  builder (parts, date) {
    const hours = +(parts[1] || parts[2] || parts[4])
    const minutes = +(parts[3] || parts[5] || 0) + 60 * (hours - (hours | 0))
    const seconds = +(parts[6] || 0) + 60 * (minutes - (minutes | 0))
    const milliseconds = 1000 * (seconds - (seconds | 0))

    // Expressions of the difference between local time and UTC of day are a component in the representations
    // ...they shall not be used as self-standing expressions.
    if (parts[7]) { // Z for UTC
      date.setTime(Date.UTC(
        date.getFullYear(),
        date.getMonth(),
        date.getDate(),
        hours | 0,
        minutes | 0,
        seconds | 0,
        milliseconds | 0
      ))
    } else {
      date.setHours(hours | 0)
      date.setMinutes(minutes | 0)
      date.setSeconds(seconds | 0)
      date.setMilliseconds(milliseconds | 0)

      // NOTE Time zones in ISO 8601 are represented as local time (with the location unspecified), as UTC, or as an offset from UTC.
      const offset = getSignFactor(parts[8]) * (+parts[9] * 60 + (+parts[10] || 0))
      correctOffset(date, offset)
    }

    return date
  }
}

export default parser

/**
 * Strict ISO8601 datetime parser
 * @module antongolub/iso8601
 */

// @flow
import type {
  IParser,
  IISOString
} from '../interface'

import YY from './yy'
import YYYYMMDD from './yyyymmdd'
import YYYYDDD from './yyyyddd'
import YYYYWWWD from './yyyywwwd'
import HHMMSSTZ from './hhmmsstz'
import DATETIME from './datetime'

const dateParsers = [YY, YYYYMMDD, YYYYDDD, YYYYWWWD]
const timeParsers = [HHMMSSTZ]
const dateTimeParsers = [DATETIME]
const allParsers = [...timeParsers, ...dateTimeParsers, ...dateParsers]

/**
 * Normalizes input string.
 * @ignore
 * @param {Mixed} value
 * @return {string}
 */
const normalizeInput = (value: IISOString) => ('' + value).replace(',', '.')

/**
 * Checks whether given date is valid.
 * @ignore
 * @param {Mixed} date
 * @return {Boolean}
 */
const isValidDate = (date: Date | number | string): boolean => {
  const instant = date instanceof Date
    ? date.getTime()
    : typeof date === 'string'
      ? Date.parse(date)
      : date

  return !isNaN(instant)
}

/**
 * @ignore
 * @param {Date/string/number} date
 * @return {Date}
 */
const normalizeDate = (date?: Date | number | string): Date => date !== undefined && isValidDate(date)
  ? new Date(date)
  : new Date()

/**
 * @ignore
 * @param {string} [group]
 * @return {IParser[]}
 */
const getParserByName = (group: string = 'all'): Array<IParser> => {
  switch (group && ('' + group).toLowerCase()) {
    case 'localtime':
    case 'time':
      return timeParsers

    case 'date':
      return dateParsers

    case 'datetime':
      return dateTimeParsers

    case 'all':
      return allParsers

    default:
      console.warn(`ISO8601: unknown parser ${group}`)
      return []
  }
}

/**
 * @ignore
 * @param {string/string[]} [group]
 * @return {IParser[]}
 */
const getParsers = (group?: string | string[]): Array<IParser> => [].concat(...[].concat(group).map(getParserByName))

/**
 * Strict ISO 8601 date parser
 * @example
 parse('1970-01-01T00:00:00.000Z');         // new Date(0)
 parse('1950-02');                          // new Date(1950, 1)
 parse('1960W011');                         // new Date(1960, 0, 4)
 parse('1950-205');                         // new Date(1950, 6, 24)
 parse('1997-W01-2')                        // new Date(1996, 11, 31)
 parse('+002015-W02-4');                    // new Date(2015, 0, 8);
 parse('2052-05-09T15:20.5');               // new Date(2052, 4, 9, 15, 20, 30, 0);

 * @module antongolub/iso8601
 * @public
 * @param {String} input
 * @param {String} [group] 'date', 'time', 'datetime' are permissible; applies all if empty
 * @param {Date/string/number} [date] Ref date for 4.2.2 Local time representations. Defaults to new Date().
 * @return {Date/undefined}
 */
export default function parse (input: IISOString, group?: string | string[], date?: Date | number | string): Date | void {
  const parsers = getParsers(group)
  const value = normalizeInput(input)
  const initialDate = normalizeDate(date)

  for (let i = parsers.length; i--;) {
    const {pattern, builder} = parsers[i]

    for (let j = pattern.length; j--;) {
      const parts = pattern[j].exec(value)

      if (parts) {
        return builder(parts, initialDate)
      }
    }
  }
}

// @flow

/**
 * @ignore
 */
export function correctOffset (date: Date, offset: number): void {
  if (!isNaN(offset)) {
    date.setMinutes(date.getMinutes() - offset - date.getTimezoneOffset())
  }
}

/**
 * @ignore
 */
export function getSignFactor (value: string): number {
  return value === '+'
    ? 1
    : -1
}

// @flow

import type {IParser} from '../interface'

// 4.1.2.3 Representations with reduced accuracy: centuries
/**
 * @ignore
 */
const parser: IParser = {
  pattern: [
    /^(\d{2}|[\-+]\d{4})$/ // NOTE There's no extended representation
  ],
  builder (parts) {
    const year = +parts[1] * 100
    return new Date(year, 0)
  }
}

export default parser

// @flow

import type {IParser} from '../interface'

// 4.1.3 Ordinal date
/**
 * @ignore
 */
const YYYYDDD: IParser = {
  pattern: [
    /^(\d{4}|[\-+]\d{6})-?(00[1-9]|0[1-9]\d|[12]\d\d|3[0-5]\d|36[0-6])$/
  ],
  builder (parts) {
    const year = +parts[1]
    const day = +parts[2]

    return new Date(year, 0, day)
  }
}

export default YYYYDDD

// @flow

import type {IParser} from '../interface'

// 4.1.2 Calendar date
/**
 * @ignore
 */
const YYYYMMDD: IParser = {
  pattern: [
    /^(\d{4}|[\-+]\d{6})(?:-(0[1-9]|1[012])(?:-(0[1-9]|[12]\d|30|31))?)?$/,     // Extended
    /^(\d{4}|[\-+]\d{6})(0[1-9]|1[012])(0[1-9]|[12]\d|30|31)$/                    // Basic
  ],
  builder (parts) {
    const year = +parts[1]
    const month = +parts[2] - 1 || 0
    const day = +parts[3] || 1

    return new Date(year, month, day)
  }
}

export default YYYYMMDD

// @flow

import type {IParser} from '../interface'

// 4.1.4 Week date
/**
 * @ignore
 */
const YYYYWWWD: IParser = {
  pattern: [
    /^(\d{4}|[\-+]\d{6})W(0[1-9]|[1-4]\d|5[0-3])([1-7])?$/,             // Basic
    /^(\d{4}|[\-+]\d{6})-W(0[1-9]|[1-4]\d|5[0-3])(?:-([1-7]))?$/      // Extended
  ],
  builder (parts) {
    const year = +parts[1]
    const week = +parts[2]
    const offset = 7 * +(new Date(year, 0, 1).getDay() < 5) + new Date(year, 0, 1).getDay() - 1
    const day = +(parts[3] || 1) + 7 * week - offset

    return new Date(year, 0, day)
  }
}

export default YYYYWWWD
